import React from "react";
import "../style/Home.css";
import ChineseTrans from '../../components/views/ChineseTrans';
import ImageBed from '../../components/views/ImageBed';
import ExportPDF from '../../components/views/ExportPDF';

import { Layout, Menu } from "antd";
import {
  MenuUnfoldOutlined,
  MenuFoldOutlined,
  InteractionOutlined,
  AreaChartOutlined,
  FilePdfOutlined
} from "@ant-design/icons";
const { Header, Sider, Content } = Layout;

export default class Home extends React.Component {
  state = {
    collapsed: false,
    showChineseTrans: false,
  };

  toggle = () => {
    this.setState({
      collapsed: !this.state.collapsed,
    });
  };

  switchModule = (item) => {
    const key = item.key
    console.log(key)
    if(key === 1) {
      console.log(this.props)
      this.props.history.push('/chineseTrans')
    }
  }

  componentDidMount() {

  }

  render() {
    return (
      <Layout>
        <Sider trigger={null} collapsible collapsed={this.state.collapsed}>
          <div className="logo" />
          <Menu theme="dark" mode="inline" defaultSelectedKeys={["1"]} onClick={this.switchModule}>
            <Menu.Item key="1" icon={<InteractionOutlined />}>
              簡繁轉換
            </Menu.Item>
            <Menu.Item key="2" icon={<AreaChartOutlined />}>
              圖床
            </Menu.Item>
            <Menu.Item key="3" icon={<FilePdfOutlined />}>
              導出 PDF
            </Menu.Item>
          </Menu>
        </Sider>
        <Layout className="site-layout">
          <Header className="site-layout-background">
            {React.createElement(
              this.state.collapsed ? MenuUnfoldOutlined : MenuFoldOutlined,
              {
                className: "trigger",
                onClick: this.toggle,
              }
            )}
            {/* <span></span> */}
          </Header>
          <Content
            className="site-layout-background"
            style={{
              margin: "24px 16px",
              padding: 20,
            }}
          >
            {this.props.children}
          </Content>
        </Layout>
      </Layout>
    );
  }
}
