import { combineReducers } from 'redux'
import defaultState from './state'

function chineseTrans(state = defaultState.chineseTrans, action) {
    switch(action.type) {
        case 'SET_CHINESE_TRANS':
            state = action.data
            break
        default:
            return state
            break
    }
}

export default combineReducers({
    chineseTrans,
})
